import{_ as e,c as i,o as d,a3 as r}from"./chunks/framework.L7k57l8l.js";const c=JSON.parse('{"title":"Hedge Fund","description":"","frontmatter":{},"headers":[],"relativePath":"blogs/CFA/C8 Alternative/M4 Hedge Fund.md","filePath":"blogs/CFA/C8 Alternative/M4 Hedge Fund.md"}'),a={name:"blogs/CFA/C8 Alternative/M4 Hedge Fund.md"};function l(n,t,o,s,u,h){return d(),i("div",null,[...t[0]||(t[0]=[r('<h1 id="hedge-fund" tabindex="-1">Hedge Fund <a class="header-anchor" href="#hedge-fund" aria-label="Permalink to &quot;Hedge Fund&quot;">​</a></h1><h2 id="characteristics" tabindex="-1">Characteristics <a class="header-anchor" href="#characteristics" aria-label="Permalink to &quot;Characteristics&quot;">​</a></h2><ul><li><p>Legal/Regulatroy</p><ul><li>Limit access to investors with minimum income or net worth</li><li>Regulatory oversights constraints are far less than those for regulated investment, except of <strong>liquid alts</strong></li></ul><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p></p><p><strong>Liquid alts</strong> are mutual funds, close-end fund, or ETF that use hedge fund-like strategies:</p><ul><li>Redeem on daily basis</li><li>More widely marketed to retail investors</li><li>Prohibited from charging an incentive fee</li><li>Significantly underperform similar strategy hedge funds. Illiquidity premium phenomenon cannot be easily transported into mutual fund format.</li></ul></div><ul><li>Few investment constraints</li><li>Large investment universe</li><li>Aggresive investment styles</li><li>Relatively free use of leverage</li><li>Constraints: Lock-up, Liquidity gates, and exit window</li><li>Relatively high fee structure: Management and incentive fees</li></ul></li></ul><h2 id="classification" tabindex="-1">Classification <a class="header-anchor" href="#classification" aria-label="Permalink to &quot;Classification&quot;">​</a></h2><ul><li>Single manager</li><li>Multi-manager <ul><li>Multi-strategy</li><li>FoF</li></ul></li></ul><table tabindex="0"><thead><tr><th><strong>Category</strong></th><th><strong>Strategies</strong></th></tr></thead><tbody><tr><td>Equity related</td><td>- Long/short equity</td></tr><tr><td></td><td>- Dedicated short selling and short-biased</td></tr><tr><td></td><td>- Equity market neutral</td></tr><tr><td>Event-driven</td><td>- Merger arbitrage</td></tr><tr><td></td><td>- Distressed securities</td></tr><tr><td>Relative value</td><td>- Fixed income arbitrage</td></tr><tr><td></td><td>- Convertible bond arbitrage</td></tr><tr><td>Opportunistic</td><td>- Global macro</td></tr><tr><td></td><td>- Managed futures</td></tr><tr><td>Specialist</td><td>- Volatility trading</td></tr><tr><td></td><td>- Reinsurance/Life settlements</td></tr><tr><td>Multi-manager</td><td>- Fund-of-funds (FOF)</td></tr><tr><td></td><td>- Multi-strategy hedge funds</td></tr></tbody></table><h2 id="strategies" tabindex="-1">Strategies <a class="header-anchor" href="#strategies" aria-label="Permalink to &quot;Strategies&quot;">​</a></h2><h3 id="equity-related-stratege" tabindex="-1">Equity Related Stratege <a class="header-anchor" href="#equity-related-stratege" aria-label="Permalink to &quot;Equity Related Stratege&quot;">​</a></h3><h4 id="long-short-construction" tabindex="-1">Long-short construction <a class="header-anchor" href="#long-short-construction" aria-label="Permalink to &quot;Long-short construction&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Strategy</th><th>Long Positions</th><th>Short Positions</th><th>Cash</th><th>Gross Exposure</th><th>Net Exposure</th></tr></thead><tbody><tr><td>Long Only</td><td>100</td><td>0</td><td>0</td><td>100</td><td>100</td></tr><tr><td>130/30 Long Extension</td><td>130</td><td>30</td><td>0</td><td>160</td><td>100</td></tr><tr><td>Market-Neutral (low risk)</td><td>50</td><td>50</td><td>100</td><td>100</td><td>0</td></tr><tr><td>Market-Neutral (high risk)</td><td>100</td><td>100</td><td>100</td><td>200</td><td>0</td></tr><tr><td>Directional (low risk)</td><td>80</td><td>40</td><td>60</td><td>120</td><td>40</td></tr><tr><td>Net Short</td><td>40</td><td>100</td><td>160</td><td>140</td><td>-60</td></tr></tbody></table><ul><li>Average exposures of 40-60 net long, given that equity markets tend to rise in long run</li><li>Reduce beta risk and provide additional alpha return potential</li><li>Can be handled both by LP and liquid alts</li><li>Liquid, diverse, with transparent mark-to-market pricing</li></ul><h4 id="dedicated-short-selling" tabindex="-1">Dedicated short-selling <a class="header-anchor" href="#dedicated-short-selling" aria-label="Permalink to &quot;Dedicated short-selling&quot;">​</a></h4><ul><li>Dedicated: Taking short-only position in equities.</li><li>Short-biased: Exposure with value-oriented and index-oriented long exposure</li><li>Activist short selling: Take short position in a given security, then publically present short report</li></ul><h2 id="portfolio-contribution-of-hedge-fund-strategies" tabindex="-1">Portfolio contribution of hedge fund strategies <a class="header-anchor" href="#portfolio-contribution-of-hedge-fund-strategies" aria-label="Permalink to &quot;Portfolio contribution of hedge fund strategies&quot;">​</a></h2>',14)])])}const f=e(a,[["render",l]]);export{c as __pageData,f as default};
