import{_ as a,c as i,o as s,a3 as r}from"./chunks/framework.CswI76e5.js";const t="/images/NCCP/precipitation.png",o="/images/NCCP/housing.png",n="/images/NCCP/small_to_all_rat.png",l="/images/NCCP/bus_distro.png",c="/images/NCCP/forest.png",d="/images/NCCP/forest_2.png",u="/images/NCCP/home_growth.png",m="/images/NCCP/home_price.png",p="/images/NCCP/rent.png",h="/images/NCCP/large_bus_distro.png",g="/images/NCCP/power.png",k=JSON.parse('{"title":"NCCP - Western North Carolina Hurricane Helene Impact Assessment","description":"","frontmatter":{"prev":{"text":"Home","link":"/"},"next":{"text":"TTS-chatbot","link":"/projects/TTS-chatbot"}},"headers":[],"relativePath":"projects/NCCP.md","filePath":"projects/NCCP.md"}'),f={name:"projects/NCCP.md"};function b(v,e,C,w,P,_){return s(),i("div",null,e[0]||(e[0]=[r('<h1 id="nccp-western-north-carolina-hurricane-helene-impact-assessment" tabindex="-1">NCCP - Western North Carolina Hurricane Helene Impact Assessment <a class="header-anchor" href="#nccp-western-north-carolina-hurricane-helene-impact-assessment" aria-label="Permalink to &quot;NCCP - Western North Carolina Hurricane Helene Impact Assessment&quot;">â€‹</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">â€‹</a></h2><p>This project focuses on assessing the impacts of Hurricane Helene on Western North Carolina. The assessment includes a high-level analysis of storm impacts, ranking of affected counties, pre-Helene conditions, and key considerations for recovery and future resiliency efforts.</p><h2 id="context" tabindex="-1">Context <a class="header-anchor" href="#context" aria-label="Permalink to &quot;Context&quot;">â€‹</a></h2><p>The analysis leveraged data from multiple authoritative sources, including NOAA, NWS, NC State Climate Office, Duke Energy, NC Forest Service, and NC Department of Agriculture. We validated affected areas and conducted a lookback study using Census and NC Commerce Department data.</p><h2 id="impact-summary" tabindex="-1">Impact Summary <a class="header-anchor" href="#impact-summary" aria-label="Permalink to &quot;Impact Summary&quot;">â€‹</a></h2><ul><li><strong>Most Impacted Counties:</strong> Buncombe, McDowell, Henderson, Rutherford, Transylvania, Polk, Yancey, Mitchell, Avery</li><li><strong>Damage Types:</strong><ul><li><strong>Power Damage:</strong> Severe damage to infrastructure with complex repair zones identified.</li><li><strong>Forest Damage:</strong> Six counties suffered heaviest damage, notably McDowell (130,805 acres) and Buncombe (89,440 acres).</li><li><strong>Flooding &amp; Landslides:</strong> Especially in Yancey, Avery, and McDowell Counties.</li></ul></li></ul><h2 id="lookback-analysis" tabindex="-1">Lookback Analysis <a class="header-anchor" href="#lookback-analysis" aria-label="Permalink to &quot;Lookback Analysis&quot;">â€‹</a></h2><p>Pre-Helene, counties exhibited varying levels of resiliency based on:</p><ul><li><strong>Demographics:</strong> Some counties had high renter concentrations and aging populations.</li><li><strong>Income &amp; Poverty Trends:</strong> Mixed income growth rates and poverty decline patterns.</li><li><strong>Housing Indicators:</strong> Several counties experienced slow home value growth and high rent burdens.</li><li><strong>Business Composition:</strong> High ratio of small businesses (&lt;$500k annual sales) in severely impacted areas like Mitchell and Yancey suggests vulnerability.</li></ul><h2 id="business-impact" tabindex="-1">Business Impact <a class="header-anchor" href="#business-impact" aria-label="Permalink to &quot;Business Impact&quot;">â€‹</a></h2><ul><li>Businesses were mapped based on size and geographical concentration.</li><li>Areas with high small business ratios are considered less resilient.</li><li>Infrastructure and power outages heavily affected small businesses, especially in rural and mountainous regions.</li></ul><h2 id="go-forward-considerations" tabindex="-1">Go-Forward Considerations <a class="header-anchor" href="#go-forward-considerations" aria-label="Permalink to &quot;Go-Forward Considerations&quot;">â€‹</a></h2><ol><li><strong>Data Buildout:</strong> Infrastructure resilience, economic health, vacancy rates, and migration patterns post-Helene.</li><li><strong>Program Identification:</strong> Map available federal, state, and local funding programs.</li><li><strong>Strategic Actions:</strong><ul><li>Support vulnerable populations.</li><li>Revitalize local businesses.</li><li>Improve resiliency against future disasters.</li><li>Implement anti-fraud measures.</li></ul></li></ol><hr><h2 id="impact-summary-1" tabindex="-1">Impact Summary <a class="header-anchor" href="#impact-summary-1" aria-label="Permalink to &quot;Impact Summary&quot;">â€‹</a></h2><div class="figure"><img src="'+t+'" alt="Map of Most Impacted Counties"><p class="caption">Map showing the most impacted counties with complex repair and disaster rebuild zones.</p></div><h2 id="lookback-analysis-1" tabindex="-1">Lookback Analysis <a class="header-anchor" href="#lookback-analysis-1" aria-label="Permalink to &quot;Lookback Analysis&quot;">â€‹</a></h2><div class="figure"><img src="'+o+'" alt="Housing Ratio Analysis"><p class="caption">Chart showing owner-to-renter ratios, home value growth, and rent burden across counties.</p></div><h2 id="business-impact-1" tabindex="-1">Business Impact <a class="header-anchor" href="#business-impact-1" aria-label="Permalink to &quot;Business Impact&quot;">â€‹</a></h2><div class="figure"><img src="'+n+'" alt="Small vs Large Business Distribution"><p class="caption">Graph illustrating the distribution of small and large businesses and their impact on resilience.</p></div><h2 id="go-forward-considerations-1" tabindex="-1">Go-Forward Considerations <a class="header-anchor" href="#go-forward-considerations-1" aria-label="Permalink to &quot;Go-Forward Considerations&quot;">â€‹</a></h2><div class="figure"><img src="'+l+'" alt="Go-Forward Recovery Framework"><p class="caption">Framework summarizing key considerations for policy and funding initiatives post-Helene.</p></div><h2 id="forest-damage-overview" tabindex="-1">Forest Damage Overview <a class="header-anchor" href="#forest-damage-overview" aria-label="Permalink to &quot;Forest Damage Overview&quot;">â€‹</a></h2><div class="figure"><img src="'+c+'" alt="Forest Damage Overview"><p class="caption">Forest damage areas across impacted counties.</p></div><h2 id="forest-damage-details" tabindex="-1">Forest Damage Details <a class="header-anchor" href="#forest-damage-details" aria-label="Permalink to &quot;Forest Damage Details&quot;">â€‹</a></h2><div class="figure"><img src="'+d+'" alt="Detailed Forest Damage"><p class="caption">Detailed breakdown of forest damage in Western NC.</p></div><h2 id="home-value-growth" tabindex="-1">Home Value Growth <a class="header-anchor" href="#home-value-growth" aria-label="Permalink to &quot;Home Value Growth&quot;">â€‹</a></h2><div class="figure"><img src="'+u+'" alt="Home Value Growth"><p class="caption">Chart showing home value growth trends by county.</p></div><h2 id="home-price-to-income-disparity" tabindex="-1">Home Price to Income Disparity <a class="header-anchor" href="#home-price-to-income-disparity" aria-label="Permalink to &quot;Home Price to Income Disparity&quot;">â€‹</a></h2><div class="figure"><img src="'+m+'" alt="Home Price Disparity"><p class="caption">Disparity between home prices and median income.</p></div><h2 id="rent-burden" tabindex="-1">Rent Burden <a class="header-anchor" href="#rent-burden" aria-label="Permalink to &quot;Rent Burden&quot;">â€‹</a></h2><div class="figure"><img src="'+p+'" alt="Rent Burden Chart"><p class="caption">Percentage of population facing high rent burdens.</p></div><h2 id="large-business-distribution" tabindex="-1">Large Business Distribution <a class="header-anchor" href="#large-business-distribution" aria-label="Permalink to &quot;Large Business Distribution&quot;">â€‹</a></h2><div class="figure"><img src="'+h+'" alt="Large Business Distribution"><p class="caption">Distribution of large businesses across impacted counties.</p></div><h2 id="power-infrastructure-damage" tabindex="-1">Power Infrastructure Damage <a class="header-anchor" href="#power-infrastructure-damage" aria-label="Permalink to &quot;Power Infrastructure Damage&quot;">â€‹</a></h2><div class="figure"><img src="'+g+'" alt="Power Infrastructure Damage"><p class="caption">Map showing power infrastructure damage zones.</p></div><h2 id="the-original-slides" tabindex="-1">The original slides <a class="header-anchor" href="#the-original-slides" aria-label="Permalink to &quot;The original slides&quot;">â€‹</a></h2><div class="resume-wrapper"><iframe src="/NCCP_slides.pdf#zoom=fitW" type="application/pdf" width="80%" height="800" style="border:none;overflow:hidden;"></iframe><div class="resume-fallback"><p>PDF preview unavailable in this view. Please download instead:</p><p><a href="/NCCP_slides.pdf" target="_blank" download>ðŸ“¥ Download Slides</a></p></div></div><p><a href="/NCCP_Slides.pdf" target="_blank" download="">ðŸ“¥ Download Report</a></p>',40)]))}const q=a(f,[["render",b]]);export{k as __pageData,q as default};
